export const BridgeInfoTab = ({ amount, sourceNetwork, destinationNetwork, fromAddress = "", toAddress = "", sourceChainIcon }: BridgeInfoTabProps) => {
  return (
    <div className="flex flex-col gap-2">
      <div className="flex flex-col divide-y divide-border rounded-xl border py-0.5 text-xs">
        {/* From Network */}
        <div className="flex items-start gap-4 py-3 px-3.5 justify-between">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              {sourceChainIcon ? (
                <img src={sourceChainIcon} alt={sourceNetwork} className="h-4 w-4 rounded-full object-cover" />
              ) : (
                <div className="h-4 w-4 rounded-xs bg-[#FF0420] flex items-center justify-center">
                  <span className="text-white text-[8px] font-bold">OP</span>
                </div>
              )}
              <span>From {sourceNetwork}</span>
            </div>
          </div>
          <div className="flex gap-1.5 items-center justify-between">
            {amount} USDC
            <img
              loading="lazy"
              className="rounded-full overflow-hidden h-4 w-4"
              alt="USDC"
              src="https://djvebdd83rbuw.cloudfront.net/tokens/usdc.png"
            />
          </div>
        </div>

        {/* To Network */}
        <div className="flex items-start gap-4 py-3 px-3.5 justify-between">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <img src={arcIcon} alt="Arc Testnet" className="h-4 w-4 rounded-full object-cover" />
              <span>To {destinationNetwork}</span>
            </div>
          </div>
          <div className="flex gap-1.5 items-center justify-between">
            {amount} USDC
            <img
              loading="lazy"
              className="rounded-full overflow-hidden h-4 w-4"
              alt="USDC"
              src="https://djvebdd83rbuw.cloudfront.net/tokens/usdc.png"
            />
          </div>
        </div>

        {/* Via */}
        <div className="flex items-start gap-4 py-3 px-3.5 justify-between">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <ViaIcon />
              <span>Via</span>
            </div>
          </div>
          <div className="flex gap-1.5 items-center justify-between">
            <span>CCTP</span>
          </div>
        </div>

        {/* From Address */}
        {fromAddress && (
          <div className="flex items-start gap-4 py-3 px-3.5 justify-between">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <FromAddressIcon />
                <span>From address</span>
              </div>
            </div>
            <span className="text-muted-foreground font-mono">
              {shortenAddress(fromAddress)}
            </span>
          </div>
        )}

        {/* To Address */}
        {toAddress && (
          <div className="flex items-start gap-4 py-3 px-3.5 justify-between">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <ToAddressIcon />
                <span>To address</span>
              </div>
            </div>
            <span className="text-muted-foreground font-mono">
              {shortenAddress(toAddress)}
            </span>
          </div>
        )}

        {/* Transfer Time */}
        <div className="flex items-start gap-4 py-3 px-3.5 justify-between">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <ClockIcon />
              <span>Transfer time</span>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <span>~30s (Fast Transfer)</span>
          </div>
        </div>
      </div>
    </div>
  );
};

